// src/internal/event.ts
function waitForEvent(el, eventName) {
  return new Promise(function (resolve) {
    function done(event) {
      if (event.target === el) {
        el.removeEventListener(eventName, done);
        resolve();
      }
    }
    el.addEventListener(eventName, done);
  });
}
export { waitForEvent };
